import {INCREMENT, DECREMENT} from './types'


export const increment =(number = 2)=> {
    return {
        type : INCREMENT,
        payload : number

    }

}


export const decrement = (number = 5)=> {
    return {
        type : DECREMENT,
        payload : number
    }
}
