import {BUY_CAKE} from './types';

const initialState = {
    numOfCakes : 10
}

const Reducer = (state = initialState, action) => {
    switch(action.type){
        case BUY_CAKE :
            return{
                ...state,
                numOfCakes : state.numOfCakes - 1
            }


        default:
            return state
    }
}


export default Reducer;
