const { INCREMENT, DECREMENT } = require("./types")

const initialState = {
    total : 10
}
const Reducer = (state = initialState, action)=> {
    switch(action.type){
        case INCREMENT:
            return{
                ...state,
                total : state.total + 1
            }
        case DECREMENT:
            return{
                ...state,
                total : state.total - 1
            }

        default:
            return state
    }
}

export default Reducer;
