import {BUY_CAKE} from './types';

const initalState = {
    numOfCake : 10
}

const reducer = (state = initalState, action )=>{
    switch(action.type){
        case BUY_CAKE:
            return{
                ...state,
                numOfCake : state.numOfCake - 1
            }

        default: {
            return state
        }
    }
}

export default reducer ;
