import {FETCH_POST} from './types';


const initialState = {
    items : [],
    loading : false,
    error : null

}
const Reducer = (state = initialState, action)=> {

    switch(action.type){
        case FETCH_POST:
            return action.payload

        default :
            return state


    }
}

export default Reducer;
