import React  from 'react';


class App extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      email : ""
    }
  }


  myChangeHandler = (e)=> {
    this.setState({email : e.target.value
    })
  }

  render(){
    return(
      <>

      <form>

          <input 
            type = "text"
            onChange = {this.myChangeHandler}
          />


      </form>

         <h3> {this.state.email} </h3>

      </>
    )
  }
}



export default App;
